mapper.update:
UPDATE 
	TBL_MIS
SET TIMES = #{times}, TITLE = #{title}, MIS_DATE = #{misDate}
WHERE
	MIS_NO = #{misNo}

mapper.select:
SELECT * FROM( SELECT ROWNUM RNUM, MIS_NO, TIMES, TITLE, MIS_DATE, MIS_REGDATE FROM ( SELECT MIS_NO, TIMES, TITLE, MIS_DATE, MIS_REGDATE FROM TBL_MIS WHERE MIS_NO > 0
ORDER BY MIS_NO DESC
)) WHERE (RNUM >= #{pageStart} AND RNUM <= #{pageEnd}) ORDER BY MIS_NO DESC
