mapper.update:
update tbl_program

set hits = hits + 1

where proNo = #{proNo}

mapper.select:
SELECT * FROM( SELECT ROWNUM RNUM, PRO_NO, PRO_NAME, APPSTART, APPEND, WRITER, HITS, TYPE, LIMIT_PARTI, REGDATE FROM ( SELECT PRO_NO, PRO_NAME, APPSTART, APPEND, WRITER, HITS, TYPE, LIMIT_PARTI, REGDATE FROM TBL_PROGRAM WHERE PRO_NO > 0
ORDER BY PRO_NO DESC
)) WHERE (RNUM >= #{pageStart} AND RNUM <= #{pageEnd}) ORDER BY PRO_NO DESC

SELECT
PRO_STATE
FROM
TBL_USER_PROGRAM
where PRO_NO = #{proNo}
AND USER_NO = #{userNo}
