mapper.update:
UPDATE
TBL_USER
SET grade = #{grade}, user_Name = #{userName},
state = #{state}
WHERE user_No = #{userNo}

mapper.select:
SELECT * FROM( SELECT rownum rnum, user_No, grade, user_Name, state FROM ( SELECT user_No, grade, user_Name, state, authority FROM TBL_USER WHERE user_No > 0 AND authority = 0
ORDER BY user_No DESC, grade DESC
)) WHERE (rnum >= #{pageStart} AND rnum <= #{pageEnd}) ORDER BY user_No DESC

SELECT COUNT(*)
FROM TBL_USER
WHERE user_no = #{userNo}
