mapper.update:
UPDATE
TBL_QnA
SET QnA_TITLE = #{qnaTitle},
QnA_CONTENTS = #{qnaContents}
WHERE
QNA_NO = #{qnaNo}

mapper.select:
SELECT * FROM( SELECT ROWNUM RNUM , QnA_NO , QnA_TITLE , QnA_CONTENTS , HITS , REGDATE , USER_NAME , USER_NO FROM ( SELECT Q.QnA_NO , Q.QnA_TITLE , Q.QnA_CONTENTS , Q.HITS , Q.REGDATE , U.USER_NAME , U.USER_NO FROM TBL_QNA Q, TBL_USER U WHERE Q.USER_NO = U.USER_NO AND Q.QnA_NO > 0
ORDER BY Q.QnA_NO DESC
)) WHERE (RNUM >=#{pageStart} AND RNUM <= #{pageEnd}) ORDER BY QnA_NO DESC

mapper.update:
update tbl_QnA

set hits = hits + 1

where QNA_NO = #{qnaNo}
